package io.com.instagram.activity;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.TextView;import io.com.instagram.R;/** * Created by rafaelcarvalho on 23/12/2017. */public class LoginActivity extends AppCompatActivity {    private TextView text_cadastro;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login);      text_cadastro = (TextView) findViewById(R.id.text_cadastro);      text_cadastro.setOnClickListener(new View.OnClickListener() {          @Override          public void onClick(View view) {              Intent i = new Intent(LoginActivity.this, CadastroActivity.class);              startActivity(i);          }      });    }}