package io.com.instagram.fragment;import android.app.Fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.ListView;import android.widget.TextView;import com.parse.FindCallback;import com.parse.ParseException;import com.parse.ParseQuery;import com.parse.ParseUser;import java.util.ArrayList;import java.util.List;import io.com.instagram.R;import io.com.instagram.adapter.UsuarioAdapter;/** * Created by rafaelcarvalho on 25/12/2017. */public class UsuarioFragment extends Fragment {    private ListView listView;    private ArrayAdapter<ParseUser> adapter;    private ArrayList<ParseUser> usuarios;    private ParseQuery<ParseUser> query;    public void UsuarioFragment(){    }    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, Bundle savedInstanceState) {        View view = super.onCreateView(inflater, container, savedInstanceState);        /**         * Montando o lista de usu√°rios         */        usuarios = new ArrayList<>();        listView = (ListView) view.findViewById(R.id.lista_usuario);        adapter = new UsuarioAdapter(getActivity(), usuarios);        listView.setAdapter( adapter );        getUser();        return view;    }    private void getUser(){        query = ParseUser.getQuery();        query.whereEqualTo("username", ParseUser.getCurrentUser().getUsername() );        query.orderByAscending("username");        query.findInBackground(new FindCallback<ParseUser>() {            @Override            public void done(List<ParseUser> objects, ParseException e) {                if(e == null ){                    if( objects.size() > 0 ){                        usuarios.clear();                        for (ParseUser u : objects){                            usuarios.add( u );                        }                        adapter.notifyDataSetChanged();                    }                }            }        });    }}