package io.com.instagram.util;import java.util.HashMap;/** * Created by rafaelcarvalho on 23/12/2017. */public class ParseErro {    private HashMap<Integer, String> erros;    public ParseErro(){        this.erros = new HashMap<>();        this.erros.put(201, "A senha não foi preenchida");        this.erros.put(202, "Usuário já existe, escolha outro usuário");    }    public String getErro(int codErros){        return this.erros.get( codErros );    }}